import "./NavBar.css"
import Model from "./Model";
import React, { useEffect } from 'react'
import { useState } from "react";
import Board from "./Board";
import './Board.css'
import Help from "./help";
import axios from 'axios'
import './inputbox.css'
import './placeholder.css'
import Popup from "./Popup";






function App() {

localStorage.setItem("played", "false")

setInterval(function(){
const event = new Date()
 const utcTime = new Date().getTime()
 const midnight = event.setUTCHours(3,51,50,0)
console.log(midnight)
console.log(utcTime)

if(utcTime === midnight)
{
  window.location.reload();
  return
}

}, 10)


const [GameStat, SetGameStat] = useState(false)
const [Attempts, SetAttempts] = useState(0)
const [divnum, setDiv] = useState(1)


const [isOpen, setIsOpen] = useState(false)
const [buttonpopup, setbuttonpopup] = useState(false)

const [tweets, setTweet] = useState()
const [answer, setAnswer] = useState()
const [answer2,setAnswer2] = useState()

useEffect(() => {

axios.get('http://localhost:4000/').then(res => {


setTweet(res.data.data.id)
setAnswer(res.data.includes.users[0].name)
setAnswer2(res.data.includes.users[0].username)
})
.catch(err => {
  console.log(err)
})


}, [])




 function getData(val)
  {



    if(val.toUpperCase().replace(/\s/g, "") === answer.toUpperCase().replace(/\s/g, ""))
    {

       let number = '' + divnum
       let div = 'div'


      document.getElementById(div + number).innerHTML = val
      document.getElementById(div + number).style.background = 'green'
      setIsOpen(true)
      console.log('you are right!')

      return
    }
    if(val.toUpperCase().replace(/\s/g, "") === answer2.toUpperCase().replace(/\s/g, ""))
    {


       let number = '' + divnum
       let div = 'div'


      document.getElementById(div + number).innerHTML = val
      document.getElementById(div + number).style.background = 'palegreen'
      setIsOpen(true)
      console.log('you are right!')

      return 
    }
    if(val !== answer)
    {
      SetAttempts(Attempts + 1)
      if(Attempts < 4)
      {

       let number = '' + divnum
       let div = 'div'


      document.getElementById(div + number).innerHTML = val

       setDiv(divnum + 1)

        console.log(val.search(/g/i))

        return
      }


    }
    if(val !== answer2)
    {
      SetAttempts(Attempts + 1)

      if(Attempts < 4)
      {


       let number = '' + divnum
       let div = 'div'



       document.getElementById(div + number).innerHTML = val

       setDiv(divnum + 1)

        console.log(val.search())

        return
      }



    }
   if(Attempts > 3)
   {
    console.log("you lose")


    setIsOpen(true)
    return
   }

   return
  }



function handleKeyPress(event)
{
if (event.key === 'Enter')
{
  let ansStore = document.getElementById('input').value

  getData(ansStore)
}
else
{

  console.log('keep going')
}

console.log(Attempts)


}

  return (
    <>
    <div className="App">
   <div className="navbar">
   <div className='help' onClick={() => setbuttonpopup(true)}><svg xmlns="http://www.w3.org/2000/svg" height="48" width="48"><path d="M24.2 35.65q.8 0 1.35-.55t.55-1.35q0-.8-.55-1.35t-1.35-.55q-.8 0-1.35.55t-.55 1.35q0 .8.55 1.35t1.35.55Zm-1.75-7.3h2.95q0-1.3.325-2.375T27.75 23.5q1.55-1.3 2.2-2.55.65-1.25.65-2.75 0-2.65-1.725-4.25t-4.575-1.6q-2.45 0-4.325 1.225T17.25 16.95l2.65 1q.55-1.4 1.65-2.175 1.1-.775 2.6-.775 1.7 0 2.75.925t1.05 2.375q0 1.1-.65 2.075-.65.975-1.9 2.025-1.5 1.3-2.225 2.575-.725 1.275-.725 3.375ZM24 44q-4.1 0-7.75-1.575-3.65-1.575-6.375-4.3-2.725-2.725-4.3-6.375Q4 28.1 4 24q0-4.15 1.575-7.8 1.575-3.65 4.3-6.35 2.725-2.7 6.375-4.275Q19.9 4 24 4q4.15 0 7.8 1.575 3.65 1.575 6.35 4.275 2.7 2.7 4.275 6.35Q44 19.85 44 24q0 4.1-1.575 7.75-1.575 3.65-4.275 6.375t-6.35 4.3Q28.15 44 24 44Zm0-3q7.1 0 12.05-4.975Q41 31.05 41 24q0-7.1-4.95-12.05Q31.1 7 24 7q-7.05 0-12.025 4.95Q7 16.9 7 24q0 7.05 4.975 12.025Q16.95 41 24 41Zm0-17Z"/></svg> </div>
    <Popup open = {buttonpopup} onClose={() => setbuttonpopup(false)}></Popup>
    <div className='home'>Tweetle</div>
    <div className='settings' onClick={() => setIsOpen(true)}>
       <svg xmlns="http://www.w3.org/2000/svg" height="48" width="48"><path d="m19.4 44-1-6.3q-.95-.35-2-.95t-1.85-1.25l-5.9 2.7L4 30l5.4-3.95q-.1-.45-.125-1.025Q9.25 24.45 9.25 24q0-.45.025-1.025T9.4 21.95L4 18l4.65-8.2 5.9 2.7q.8-.65 1.85-1.25t2-.9l1-6.35h9.2l1 6.3q.95.35 2.025.925Q32.7 11.8 33.45 12.5l5.9-2.7L44 18l-5.4 3.85q.1.5.125 1.075.025.575.025 1.075t-.025 1.05q-.025.55-.125 1.05L44 30l-4.65 8.2-5.9-2.7q-.8.65-1.825 1.275-1.025.625-2.025.925l-1 6.3ZM24 30.5q2.7 0 4.6-1.9 1.9-1.9 1.9-4.6 0-2.7-1.9-4.6-1.9-1.9-4.6-1.9-2.7 0-4.6 1.9-1.9 1.9-1.9 4.6 0 2.7 1.9 4.6 1.9 1.9 4.6 1.9Zm0-3q-1.45 0-2.475-1.025Q20.5 25.45 20.5 24q0-1.45 1.025-2.475Q22.55 20.5 24 20.5q1.45 0 2.475 1.025Q27.5 22.55 27.5 24q0 1.45-1.025 2.475Q25.45 27.5 24 27.5Zm0-3.5Zm-2.2 17h4.4l.7-5.6q1.65-.4 3.125-1.25T32.7 32.1l5.3 2.3 2-3.6-4.7-3.45q.2-.85.325-1.675.125-.825.125-1.675 0-.85-.1-1.675-.1-.825-.35-1.675L40 17.2l-2-3.6-5.3 2.3q-1.15-1.3-2.6-2.175-1.45-.875-3.2-1.125L26.2 7h-4.4l-.7 5.6q-1.7.35-3.175 1.2-1.475.85-2.625 2.1L10 13.6l-2 3.6 4.7 3.45q-.2.85-.325 1.675-.125.825-.125 1.675 0 .85.125 1.675.125.825.325 1.675L8 30.8l2 3.6 5.3-2.3q1.2 1.2 2.675 2.05Q19.45 35 21.1 35.4Z"/></svg></div>
        </div>
    </div>
    <Model text ={'You completed the Tweetle in ' + (Attempts + 1) + " Tries"} open={isOpen} onClose={() => setIsOpen(false)}>
      </Model>
    <Board number = {tweets}></Board>
    <div className="wrapper">
    <input id='input' type= "text" onKeyDown={handleKeyPress}/>
    </div>
    <div className="placewrap">

    <div className="box1" id="div1"></div>
    <div className="box2" id="div2"></div>
    <div className="box3" id="div3"></div>
    <div className="box4" id="div4"></div>

    </div>
    </>

  );
}



export default App;
